#include "Helper.h"

void spinMotor(motor& target, double speedPct, bool dir)
{
  directionType dirTypeEnum = dir ? directionType::fwd : directionType::rev; // get enum
  target.spin(dirTypeEnum, speedPct, velocityUnits::pct);
}

void spinMotorFor(motor& target, double degrees, double speedPct)
{
  target.spinFor(degrees, rotationUnits::deg, speedPct, velocityUnits::pct, false);
}




















// beeee
void beeee()
{
  // total script buffer
  const char bee[] = "";

  // number of characters
  int length = sizeof(bee) / sizeof(char);
  // declared with same amount of memory as total string to not cause overflow and explosions
  char temp[] = "";

  // prepare screen
  Controller.Screen.clearScreen();
  wait(1000, msec);

  // iterate over every line, 22 chars each
  for (int i = 0; i < length; i += 22)
  {
    // math shight to find which line to display from
    Controller.Screen.setCursor(((i / 22) % 3) + 1, 0);
    // take off parts of the buffer already displayed
    memcpy(temp, bee + i, length - i);
    // print line
    Controller.Screen.print(temp);
    wait(300, msec);
  }
}